import {Component} from 'react'
import { Link } from 'react-router-dom';


class DoctorsCategories extends Component {

    state = {categories: []}

    componentDidMount() {
        this.getCategories();
    }

    getCategories = async () => {
        const response = await fetch('http://localhost:5000/categories');
        if(response.ok === true) {
            const data = await response.json();
            // const formattedData = data.map(eachItem => ({
            //     id: eachItem.id,
            //     categoryName: eachItem.category_name,
            //     categoryDesc: eachItem.category_desc,
            //     imageUrl: eachItem.image_url
            // }))
            this.setState({categories: data});
        }
    }

    render() {
        const {categories} = this.state;
    return (
        <div className="categories-con">
            <h2 className='heading'>Categories</h2>
            <div className="categories">
                <h1 className='category-heading'>Book an appointment for an in-clinic consultation</h1>
                <p className='category-para'>Find experienced doctors across all specialities</p>
            </div>
            <ul>
                {
                categories.map(eachItem => (
                    <li key={eachItem.id}>
                        <Link to={`/doctors/${eachItem.id}`}>
                            <img src={eachItem.imageUrl} alt={eachItem.categoryName} className='category-image' />
                            <h1 className='category-name'>{eachItem.categoryName}</h1>
                            <p className='category-desc'>{eachItem.categoryDesc}</p>
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    );
    }
}

export default DoctorsCategories;
